messageHandlers.script:

headers = ["Device Tag Path", "Data Type", "Device Value", "Quality", "Active Alarm"]
data = []

tag_folder_path = self.view.custom.parentTagPath
res = system.tag.browse(tag_folder_path, filter={"recursive":True, "tagType":"AtomicTag"})

for result in res.getResults():
	path = str(result['fullPath'])
	anyAlarmPath = path + '/Alarms.HasActive'
	dataType = (result['dataType'])
	deviceValue = str((result['value']).value)
	deviceQuality = str((result['value']).quality)
	anyAlarm = system.tag.readBlocking(anyAlarmPath)[0].value

	if "Alarm Summary" in path:
		alarmSummary = True
	else:
		alarmSummary = False

	if deviceValue == 'True':
		deviceValue = '1'
	else:
		if deviceValue == 'False':
			deviceValue = '0'	

	if anyAlarm == True:
		anyAlarm = '1'
	else:
		anyAlarm = '0'				


	if (deviceQuality != 'Bad_Disabled') and (alarmSummary == False):
		data.append([path,dataType, deviceValue, deviceQuality, anyAlarm])
	#data.append([result["fullPath"])

# Finally, both the headers and data lists are used in the function to create a Dataset object
self.view.custom.instances = system.dataset.toDataSet(headers, data)
