messageHandlers.script:
import traceback
try:
	def sanitise_tree(element):
			if hasattr(element, '__iter__'):
				if hasattr(element, 'keys'):
					return dict(zip(element.keys(), (sanitise_tree(element[k] for k in element.keys()))))
				else:
					return list(sanitise_tree(x) for x in element)
			return element

	area = payload['area']
	site = payload['site']
	name = payload['name']
	scope = payload['scope']

	if scope == 1:
		username = 'SuperConf'
	else:
		username = self.session.props.auth.user.userName

	chart_props = self.parent.parent.parent.getChild("powerChart").getChild("PowerChart").props
	chart_props_sanitised = sanitise_tree(chart_props)
	chart_props_json = system.util.jsonEncode(chart_props_sanitised)

	params = {'area':area, 'site':site, 'trendname':name, 'username':username, 'powerChartPropsJSON':chart_props_json}
	result = system.db.runNamedQuery('trends/saveTrend', params)
	system.perspective.print(result)
	system.perspective.sendMessage('refresh-table', scope='page')
except Exception, e:
	tb = traceback.format_exc()
	system.perspective.print(tb)

